function bsimpan(id, text) {
    $('#' + id).html('<i class="fa-solid fa-floppy-disk"></i> ' + text);
}

function messages(msg) {
    $.each(msg, function(key, val) {
        // console.log(key);
        $('.' + key + '_err').text(val);
    });
}

function reset() {
    location.reload(true);
}

function closeModal(idModal) {
    $(idModal).modal('hide');
}

function openModal(idModal) {
    $(idModal).modal('show');
}

function selectOp(kode) {
    $('#' + kode).select2();
}

function selectOpTag(kode) {
    $('#' + kode).select2({
        tags: true,
    });
}

function selectOpReq(kode) {
    $('#' + kode).addClass('required_sel');
    $('#' + kode).select2();
}

function selectOpReqTag(kode) {
    $('#' + kode).addClass('required_sel');
    $('#' + kode).select2({
        tags: true,
    });
}

function selectServerSide(kode, url, data) {
    $('#' + kode).select2({
        ajax: {
            url: url,
            dataType: 'json',
            delay: 250,
            processResults: data,
            cache: true
        }
    });
}

function selectServerSideReq(kode, url, data) {
    $('#' + kode).addClass('required_sel');
    $('#' + kode).select2({
        ajax: {
            url: url,
            dataType: 'json',
            delay: 250,
            processResults: data,
            cache: true
        }
    });
}

function setText(kode, value) {
    $('#' + kode).val(value);
}

function setTextReq(kode) {
    $('#' + kode).val();
    $('#' + kode).addClass('required_sel');
}

function setSelect(kode, value) {
    $('#' + kode).val(value);
}

function getText(kode) {
    $('#' + kode).val();
}

function getSelect(kode) {
    $('#' + kode).val();
}

function setTextReadOnly(kode, kondisi) {
    $('#' + kode).prop('readonly', kondisi);
    $('#' + kode).addClass('required_sel');
}

function setSelectReadOnly(kode, kondisi) {
    $('#' + kode).prop('disabled', kondisi);
    $('#' + kode).addClass('required_sel');
}

function logSelect(id) {
    $('#' + id).change(function() {
        var cek = $(this).val();
        console.log(cek);
    });
}

function logInput(id) {
    $('#' + id).keyup(function() {
        var cek = $(this).val();
        console.log(cek);
    });
}