function viewPdf(filePdf, idCanvas, go_previous, go_next, current_page, tot_page, zoom_in, zoom_out) {
    $('#' + filePdf).change(function(e) {
        var file = e.target.files[0];
        var fileReader = new FileReader();
        // console.log(file);
        fileReader.onload = function() {
            // console.log(this.result);
            pdfConfig(
                this.result,
                idCanvas,
                go_previous,
                go_next,
                current_page,
                tot_page,
                zoom_in,
                zoom_out
            );
        }
        fileReader.readAsDataURL(file);
    });
}

function pdfConfig(filePdf, idCanvas, go_previous, go_next, current_page, tot_page, zoom_in, zoom_out) {
    var myState = {
        pdf: null,
        currentPage: 1,
        scale: 1,
        zoom: 1
    }

    pdfjsLib.getDocument(filePdf).then((pdf) => {

        myState.pdf = pdf;
        $('#' + tot_page).val(pdf.numPages);
        // console.log(pdf.numPages);
        render();

    });

    function render() {
        myState.pdf.getPage(myState.currentPage).then((page) => {

            var canvas = document.getElementById(idCanvas);
            var ctx = canvas.getContext('2d');

            var viewport = page.getViewport(myState.zoom);

            canvas.width = viewport.width;
            canvas.height = viewport.height;

            page.render({
                canvasContext: ctx,
                viewport: viewport
            });
        });
    }

    $('#' + go_previous).click(function(e) {
        e.preventDefault();
        if (myState.pdf == null || myState.currentPage == 1) {
            return;
        } else {
            myState.currentPage -= 1;
            $('#' + current_page).val(myState.currentPage);
            render();
            return;
        }
    });

    $('#' + go_next).click(function(e) {
        e.preventDefault();
        if (myState.pdf == null || myState.currentPage >= myState.pdf._pdfInfo.numPages) {
            return;
        } else {
            myState.currentPage += 1;
            $('#' + current_page).val(myState.currentPage);
            render();
            return;
        }
    });

    $('#' + current_page).keypress(function(e) {
        e.preventDefault();
        if (myState.pdf == null) {
            return;
        } else {
            // Get key code
            var code = (e.keyCode ? e.keyCode : e.which);

            // If key code matches that of the Enter key
            if (code == 13) {
                var desiredPage = document.getElementById(current_page).valueAsNumber;

                if (desiredPage >= 1 && desiredPage <= myState.pdf._pdfInfo.numPages) {
                    myState.currentPage = desiredPage;
                    $('#' + current_page).val(desiredPage);
                    render();
                }
            }
            render();
            return;
        }
    });

    $('#' + zoom_in).click(function(e) {
        e.preventDefault();
        if (myState.pdf == null) {
            return;
        } else {
            myState.zoom += 0.5;
            render();
            return;
        }
    });

    $('#' + zoom_out).click(function(e) {
        e.preventDefault();
        if (myState.pdf == null) {
            return;
        } else {
            myState.zoom -= 0.5;
            render();
            return;
        }
    });
}

function submitImportSoc(idForm, idBtn, title, urlAjax, idVal, resSs) {
    $('#' + idForm).submit(function(e) {
        e.preventDefault();
        var formData = new FormData(this);
        bsimpan(idBtn, 'Please wait..');
        Swal.fire({
            title: title,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ya, simpan!'
        }).then((result) => {
            if (result.isConfirmed) {
                // var status = result.value;
                $.ajax({
                    url: urlAjax,
                    type: "POST",
                    data: formData,
                    contentType: false,
                    processData: false,
                    cache: false,
                    beforeSend: function() {
                        bsimpan(idBtn, 'Please wait..');
                    },
                    success: function(res) {
                        console.log(res);
                        if ($.isEmptyObject(res.error)) {
                            if (res.success) {
                                setText(idVal, res.kode);
                                Swal.fire(
                                    'Berhasil!',
                                    res.success,
                                    'success'
                                ).then(resSs);
                            } else {
                                Swal.fire(
                                    'Gagal!',
                                    res.errors,
                                    'error'
                                ).then((res) => {
                                    bsimpan(idBtn, 'Simpan');
                                });
                            }
                        } else {
                            bsimpan(idBtn, 'Simpan');
                            Swal.fire(
                                'Oops...!',
                                'Field harus ter isi!',
                                'error'
                            );
                            messages(res.error);
                        }
                    },
                    complete: function(data) {
                        bsimpan(idBtn, 'Simpan');
                    },
                    error: function(err) {
                        bsimpan(idBtn, 'Simpan');
                        console.log('Error:', err);
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Terjadi kesalahan!',
                        });
                    }
                });
            } else {
                bsimpan(idBtn, 'Simpan');
            }
        });
    });
}

