function bsimpan(id, text) {
    $('#'+id).html('<i class="fa-solid fa-floppy-disk"></i> '+text);
}

function messages (msg) {
    $.each( msg, function( key, val ) {
        // console.log(key);
        $('.'+key+'_err').text(val);
    });
}

function reset() {
    location.reload(true);
}

